import React from 'react'
import { HashLink as Link } from 'react-router-hash-link';
import { gql , useMutation } from '@apollo/client';

const addNewMovie = gql`
  mutation AddNewMovie($name: String!, $genre: String!, $year: String) {
    addMovie(name: $name, genre: $genre, year: $year) {
      name
      genre
      year
  }
  }
`
const AddMovie = () => {
  const submitMovie = (event) => {
    event.preventDefault()
    console.log("movie added")
  }
  const [addTodo, { data, loading, error }] = useMutation(ADD_TODO);

  if (loading) return 'Submitting...';
  if (error) return `Submission error! ${error.message}`;
    return (
        <div className="addMovie" id="addNewMovie">
        <form onSubmit={submitMovie}>
            <input type="text" placeholder="Movie Name" required/>
            <input type="text" placeholder="Movie Genre" required/>
            <input type="text" placeholder="Movie Year" />
            <button>Add Movie</button>
        </form>
        <div className='top'>
            <Link smooth to="#header" className="topButton">Back to top</Link>
        </div>
        </div>
    )
    }
    export default AddMovie
