import React, {useState } from 'react'
import { HashLink as Link } from 'react-router-hash-link';
import { gql , useMutation } from '@apollo/client';

const addNewMovie = gql`
  mutation AddNewMovie($name: String!, $genre: String!, $year: String) {
    addMovie(name: $name, genre: $genre, year: $year) {
      name
      genre
      year
  }
  }
`
const AddMovie = () => {
  const submitMovie = (event) => {
    event.preventDefault()
    console.log("movie added")
  }
  const [movieName, setMovieName] = useState('');
  const [movieGenre, setMovieGenre] = useState('');
  const [movieYear, setMovieYear] = useState('');
  const [newMovieData, { data, loading, error }] = useMutation(addNewMovie);

  if (loading) return 'Submitting your movie...';
  if (error) return `Cannot add your movie! ${error.message}`;
    return (
        <div className="addMovie" id="addNewMovie">
        <form onSubmit={submitMovie}>
            <input type="text" placeholder="Movie Name" required onChange={(e) => setMovieName(e.target)} />
            <input type="text" placeholder="Movie Genre" required onChange={(e) => setMovieGenre(e.target)}/>
            <input type="text" placeholder="Movie Year" onChange={(e) => setMovieYear(e.target)}/>
            <button>Add Movie</button>
        </form>
        <div className='top'>
            <Link smooth to="#header" className="topButton">Back to top</Link>
        </div>
        </div>
    )
    }
    export default AddMovie
